insert into cursos (id_curso, curso, descripcion, costo, id_docente, docente, sesiones, duracion_sesion, calificacion, fecha_creacion) values (1, 'Biotechnology: Biological Products', 'Biotechnology is the use of biology to solve problems and make useful products. The most prominent approach used is genetic engineering, which enables scientists to tailor an organisms DNA at will.', 30000, '100722', 'Nikolaus Mulchrone', 26, 73, 3.2, '2022-04-01');
insert into cursos (id_curso, curso, descripcion, costo, id_docente, docente, sesiones, duracion_sesion, calificacion, fecha_creacion) values (2, 'Artificial Intelligence and Machine Learning', 'AI applications include advanced web search engines, recommendation systems, understanding human speech, self-driving cars, automated decision-making and competing at the highest level in strategic game systems.', 34000, '299872', 'Ruggiero Simecek', 18, 72, 3.6, '2022-02-19');
insert into cursos (id_curso, curso, descripcion, costo, id_docente, docente, sesiones, duracion_sesion, calificacion, fecha_creacion) values (3, 'Project Management', 'To understand project management, we must look deeper into what constitutes a project. Essentially, projects are temporary efforts to create value through unique products, services, and processes.', 24000, '100823', 'Juanita Lauder', 30, 84, 1.9, '2022-02-12');
insert into cursos (id_curso, curso, descripcion, costo, id_docente, docente, sesiones, duracion_sesion, calificacion, fecha_creacion) values (4, 'Computer Software: Prepackaged Software', 'Software is a set of instructions, data or programs used to operate computers and execute specific tasks. It is the opposite of hardware, which describes the physical aspects of a computer.', 17000, '199028', 'Chadwick Torr', 29, 113, 3.8, '2022-11-22');
insert into cursos (id_curso, curso, descripcion, costo, id_docente, docente, sesiones, duracion_sesion, calificacion, fecha_creacion) values (5, 'Python programming', 'Python is an interpreted, object-oriented, high-level programming language with dynamic semantics developed by Guido van Rossum. It was originally released in 1991.', 34000, '100723', 'Marianna Corse', 26, 75, 3.4, '2022-08-13');
insert into cursos (id_curso, curso, descripcion, costo, id_docente, docente, sesiones, duracion_sesion, calificacion, fecha_creacion) values (6, 'Fine Art', 'Creative art, especially visual art whose products are to be appreciated primarily or solely for their imaginative, aesthetic, or intellectual content.', 15000, '209888', 'Gordy Albarez', 8, 83, 4.6, '2022-10-29');
insert into cursos (id_curso, curso, descripcion, costo, id_docente, docente, sesiones, duracion_sesion, calificacion, fecha_creacion) values (7, 'Property-Casualty Insurers', 'P&C are companies that provide coverage on assets, as well as liability insurance for accidents, injuries, and damage to others or their belongings.', 26000, '100923', 'Ingra Giblin', 8, 64, 4.6, '2022-10-07');
insert into cursos (id_curso, curso, descripcion, costo, id_docente, docente, sesiones, duracion_sesion, calificacion, fecha_creacion) values (8, 'Oil & Gas Production', 'Oil and gas production is the general process of manufacturing oil and natural gas from wells and turning them into the final petroleum products that consumers can use.', 27000, '298332', 'Susie Edgeson', 13, 114, 4.0, '2022-01-10');
insert into cursos (id_curso, curso, descripcion, costo, id_docente, docente, sesiones, duracion_sesion, calificacion, fecha_creacion) values (9, 'Semiconductors', 'A semiconductor is a material, which has an electrical conductivity value falling between that of a conductor, such as copper, and an insulator, such as glass. Its resistivity falls as its temperature rises.', 20000, '201334', 'Doralynn Mortimer', 19, 113, 4.9, '2022-04-24');
