insert into cursos (id_curso, curso, descripcion, costo, id_docente, docente, sesiones, duracion_sesion, calificacion, fecha_creacion) values (15, 'Computer Manufacturing', 'Major companies include US-based Apple, Dell, Hewlett Packard Enterprise, and IBM, as well as ASUS (Taiwan), Canon (Japan), and Lenovo (Hong Kong).', 22000, '323112', 'Bale Lanfare', 23, 95, 4.5, '2021-12-22');
